<?php

namespace App\Admin\Actions;

use App\Models\Examination;
use Encore\Admin\Actions\RowAction;
use Encore\Admin\Admin;
use Illuminate\Database\Eloquent\Model;
use Illuminate\Support\Facades\App;

class generateReport extends RowAction
{
    public $name = '生成报告';

    public function handle(Model $model)
    {
        $examination = Examination::find($model->id);
        if(!empty($examination->report_file)) {
            return $this->response()->success('报告已生成！')->refresh();
        }

        $snappy = App::make('snappy.pdf');

        // 从 HTML 字符串生成 PDF
        //$html = '<h1>dongxuemin</h1>';
        //$snappy->generateFromHtml($html, '/storage/app/report/report'.$examination->school_id.'.pdf');

        $snappy->generate(config('app.url')."/admin/schoolReportPage/".$examination->school_id, '/storage/app/report/report'.$examination->school_id.'.pdf');

        $examination->report_file = '/storage/app/report/report'.$examination->school_id.'.pdf';
        $examination->save();
        
        return $this->response()->success('报告已生成！')->refresh();
    }


    // 上传表单
    public function form()
    {
        //$this->file('file', '上传excel')->rules('required', ['required' => '文件不能为空']);
    }

    /**
     * @return string
     * 上传效果
     */
    public function handleActionPromise()
    {
        $resolve = <<<'SCRIPT'
var actionResolverss = function (data) {
            $('.modal-footer').show()
            $('.tips').remove()
            var response = data[0];
            var target   = data[1];

            if (typeof response !== 'object') {
                return $.admin.swal({type: 'error', title: 'Oops!'});
            }

            var then = function (then) {
                if (then.action == 'refresh') {
                    $.admin.reload();
                }

                if (then.action == 'download') {
                    window.open(then.value, '_blank');
                }

                if (then.action == 'redirect') {
                    $.admin.redirect(then.value);
                }
            };

            if (typeof response.html === 'string') {
                target.html(response.html);
            }

            if (typeof response.swal === 'object') {
                $.admin.swal(response.swal);
            }

            if (typeof response.toastr === 'object') {
                $.admin.toastr[response.toastr.type](response.toastr.content, '', response.toastr.options);
            }

            if (response.then) {
              then(response.then);
            }
        };

        var actionCatcherss = function (request) {
            $('.modal-footer').show()
            $('.tips').remove()

            if (request && typeof request.responseJSON === 'object') {
                $.admin.toastr.error(request.responseJSON.message, '', {positionClass:"toast-bottom-center", timeOut: 10000}).css("width","500px")
            }
        };
SCRIPT;

        Admin::script($resolve);

        return <<<'SCRIPT'
         $('.modal-footer').hide()
         let html = `<div class='tips' style='color: red;font-size: 18px;'>请耐心等待结果不要关闭窗口！<img src="data:image/gif;base64,"><\/div>`
         $('.modal-header').append(html)
process.then(actionResolverss).catch(actionCatcherss);
SCRIPT;
    }


}
